mapper.select:
select count(articleno) from happyhouse.notice

	
		where subject like concat('%', #{word}, '%')
	
	
		where ${key} = #{word}

select articleno, subject, content, regidate from
happyhouse.notice
where articleno = #{articleno}

mapper.update:
update notice
set subject = #{subject}, content = #{content}, regidate = now()
where articleno = #{articleno}
