mapper.select:
select articleno,author,subject,regidate from qnanotice
order by articleno desc
limit 0,4;

select articleno, author, subject, content, regidate 
from qnanotice
where articleno = #{articleno}

mapper.update:
update qnanotice
set subject = #{subject}, content = #{content}, regidate = now()
where articleno = #{articleno}
